class Solution {
public:
    int solve(int i, int x, vector<int>& items, vector<vector<int>>& dp) {
        if(x == 0) return 0; 
        if(x < 0 || i == items.size()) return 1'000'000;
        if(dp[i][x] != -1) return dp[i][x]; 
        return dp[i][x] = 1 + min(solve(i+1, x, items, dp), solve(i+1, x-items[i], items, dp));
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        int n = nums.size();
        for(int i = 0; i < n; i++) {
            vector<int> items;
            vector<int> indices; 
            indices.push_back(0); 
            for(int j = 0; j < queries.size(); j++) {
                auto &query = queries[j]; 
                int left = query[0], right = query[1], val = query[2]; 
                if(left > i || right < i) continue; 
                items.push_back(val);
                indices.push_back(j+1);
            }
            vector<vector<int>> dp(items.size(), vector<int>(1001, -1)); 
            int tmp = solve(0, nums[i], items, dp);
            if(tmp >= 1'000'000) return -1; 
            res = max(res, indices[tmp]);
        }
        return (res >= 1'000'000 ? -1 : res);
    }
};

