class Solution {
public:

    int find_min_idx(vector<int> &v, int sum, int i, int memo[1001][1001]){
        int n = v.size();

        if(i == n){
            if(sum == 0){
                return n;
            } else{
                return INT_MAX;
            }
        }

        if(sum<0){
            return INT_MAX;
        }

        if(sum == 0){
            return i;
        }

        if(memo[sum][i]!=-1) return memo[sum][i];

        return memo[sum][i] = min(find_min_idx(v, sum, i+1, memo), find_min_idx(v, sum- v[i], i+1, memo));
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();
        int q = queries.size();

        vector<vector<int>> v(n, vector<int>(q, 0));

        for(int i = 0; i<q; ++i){
            int left = queries[i][0], right = queries[i][1], val = queries[i][2];

            for(int j = left; j<=right; ++j){
                v[j][i] = val;
            }
        }

        int ans = INT_MIN;
        int memo[1010][1001];
        for(int i = 0; i<n; ++i){
            memset(memo, -1, sizeof(memo));
            ans = max(find_min_idx(v[i], nums[i], 0, memo), ans);
        }

        return ans == INT_MAX? -1: ans;
    }
};

