class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& que) {
        int l = -1, r = que.size() - 1;
        int n = a.size();
        int ans = -1;
        int mx = 0;
        for (int x : a) {
            mx = max(x, mx);
        }

        while (l <= r) {
            int mid = (l + r) / 2;
            vector<vector<int>> dp(n, vector<int>(mx + 1, 0));
            for (int id = 0; id < n; id++) {
                dp[id][0] = 1;
            }
            for (int q = 0; q <= mid; q++) {
                int l = que[q][0];
                int r = que[q][1];
                int val = que[q][2];
                for (int v = mx; v >= val; v--) {
                    for (int id = l; id <= r; id++) {
                        dp[id][v] |= dp[id][v - val];
                    }
                }
            }     
            int cnt = 0;
            for (int i = 0; i < n; i++) {
                cnt += dp[i][a[i]];
            }
            if (cnt == n) {
                r = mid - 1;
                ans = mid + 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

