class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        dp = [[float('inf')] * 1001 for _ in range(n)]
        for i in range(n):
            dp[i][0] = 0
        if all(dp[i][nums[i]] != float('inf') for i in range(n)):
            return 0
        for k, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                num = nums[i]
                for j in range(num, val - 1, -1):
                    dp[i][j] = min(dp[i][j], dp[i][j - val] + 1)
            if all(dp[i][nums[i]] != float('inf') for i in range(n)):
                return k + 1
        return -1

