class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        l = len(nums)
        inf = float('inf')
        qm = []
        for li, ri, val in queries:
            oq = [0] * l
            for i in range(li, ri+1):
                oq[i] += val
            qm.append(oq)
        #print(queries)
        #print(qm)
        lb = -1
        ub = len(queries) - 1
        def isz(n, cnts):
            flags = [True] + [False] * n
            for c in cnts:
                inds = []
                for i in range(len(flags)-1, -1, -1):
                    if flags[i] and i + c < len(flags):
                        flags[i + c] = True
                if flags[n]:
                    return True
            return flags[n]
        ans = inf
        while lb <= ub:
            mi = (lb+ub) // 2
            found = True
            for i in range(l):
                n = nums[i]
                q = list(qm[j][i] for j in range(mi+1))
                if not isz(n, q):
                    found = False
                    break
            if found:
                ans = min(ans, mi)
                ub = mi - 1
            else:
                lb = mi + 1
        return ans + 1 if ans != inf else -1

