class Solution {
public:
    bool canSumTo(vector<int> &seq, int target) {
        int n = seq.size();
        if (n==0) return target==0;

        unordered_set<int> dp, tmp;
        dp.insert(0);
        for (int &x: seq) {
            for (int sm: dp) {
                tmp.insert(sm+x);
            }
            for (int y:tmp) {
                dp.insert(y);
            }
            tmp.clear();
        }




        return dp.find(target)!=dp.end();
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int k = 0;

        int n = nums.size();
        int done = 0;
        vector<unordered_set<int>> dp(n);
        for (int i=0;i<n;i++) {
            if (nums[i]==0) done |= (1<<i);
            dp[i].insert(0);
        }
        if (done+1==(1<<n)) return k;

        for (auto &q:queries) {
            k++;
            int l = q[0], r = q[1], d = q[2];
            for (int i =l;i<=r;i++) {
                if (done&(1<<i)) continue;
                unordered_set<int> tmp;
                for (int x: dp[i]) {
                    tmp.insert(x+d);
                }
                for (int x:tmp) {
                    dp[i].insert(x);
                }
                if (dp[i].find(nums[i])!=dp[i].end()) {
                    done |= (1<<i);
                }
            }
            if (done+1==(1<<n)) return k;
        }
        return -1;
    }
};

