public class Solution {
    public int MinZeroArray(int[] nums, int[][] queries) 
    {
        int max = 0;
        for (int i = 0; i < nums.Length; ++i) 
        {
            int x = nums[i];

            HashSet<int> hset = new() {x};
            int j = 0;
            for (; j < queries.Length && !hset.Contains(0); ++j) 
            {
                var q = queries[j];

                if (i < q[0] || i > q[1]) continue;

                foreach (int val in hset.ToArray()) if (val - q[2] >= 0) hset.Add(val - q[2]);
            }

            if (!hset.Contains(0)) return -1;

            max = Math.Max(max, j);
        }
        return max;
    }
}

