class Solution:
    def is_possible(self, nums, dp, k, target):

        first = False
        second = False
        for i in range(target + 1):

            if k - 1 >= 0:
                first = dp[k - 1][i]

            if i - nums[k - 1] >= 0 and k - 1 >= 0:
                second = dp[k - 1][i - nums[k - 1]]

            dp[k][i] = first or second

        return dp[k][target]

    def get_min_k(self, index, queries, nums):
        dp = [[False for _ in range(nums[index] + 1)] for _ in range(len(queries) + 1)]

        for i in range(len(dp)):
            dp[i][0] = True

        numbers = []
        k = 0
        for l, r, v in queries:
            k += 1
            if l <= index and r >= index:
                numbers.append(v)
                if self.is_possible(numbers, dp, len(numbers), nums[index]):
                    return k
        return -1

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ans = 0

        for i in range(len(nums)):
            if nums[i] != 0: 
                k = self.get_min_k(i, queries, nums)
                if k == -1:
                    return -1
                ans = max(ans, k)
        return ans

