class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        f = []
        for i in range(n):
            f.append(set())
        is_checked = [0] * n
        for i in range(n):
            if nums[i] == 0:
                is_checked[i] = 1
        if sum(is_checked)==n:
            return 0
        for i in range(len(queries)):
            l = queries[i][0]
            r = queries[i][1]
            val = queries[i][2]
            for idx in range(l, r+1):
                current_sums = list(f[idx])
                for s in current_sums:
                    f[idx].add(s+val)
                f[idx].add(val)
                if nums[idx] in f[idx]:
                    is_checked[idx] = 1
            if sum(is_checked) == n:
                return i+1
        return -1

