class Solution {
public:
    int helper(int target, int curr, int idx, vector<vector<int>>& queries, vector<vector<int>>& dp, int index)
    {
        if(idx == queries.size())
        {
            return 1001;
        }

        if(dp[idx][curr] != -1) return dp[idx][curr];

        int l = queries[idx][0];
        int r = queries[idx][1];
        int w = queries[idx][2];

        if(index < l || index > r)
        {
            return dp[idx][curr] = helper(target, curr, idx + 1, queries, dp, index);
        }

        if(curr + w < target)
        {
            dp[idx][curr] = min(helper(target, curr + w, idx + 1, queries, dp, index), helper(target, curr, idx +1, queries, dp, index));
            return dp[idx][curr];
        }

        if(curr + w > target)
        {
            return dp[idx][curr] = helper(target, curr, idx + 1, queries, dp, index);
        }

        return dp[idx][curr] = idx + 1;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> count(n + 1, 0);
        int ans = 0;
        for(int i = 0; i < n; ++i)
        {
            int numQ = queries.size();
            vector<vector<int>> dp(numQ, vector<int>(1001, -1));
            int curr = 0;
            if(nums[i] != 0)
            {
                curr = helper(nums[i], 0, 0, queries, dp, i);
            }
            ans = max(ans, curr);
        }
        if(ans > 1000)
        {
            return -1;
        }
        return ans;
    }
};

