#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

class Solution
{
public:
    bool canMakeZero(vector<int> &nums, vector<vector<int>> &allowedOps)
    {
        int n = nums.size();
        for (int i = 0; i < n; i++)
        {
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int val : allowedOps[i])
            {
                for (int j = target; j >= val; j--)
                {
                    dp[j] = dp[j] || dp[j - val];
                }
            }
            if (!dp[target])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int> &nums, vector<vector<int>> &queries)
    {
        int n = nums.size();
        int t = queries.size();
        int low = 0, high = t, ans = -1;

        while (low <= high)
        {
            int mid = (low + high) / 2;
            vector<vector<int>> allowedOps(n);

            for (int i = 0; i < mid; i++)
            {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for (int idx = l; idx <= r; idx++)
                {
                    allowedOps[idx].push_back(val);
                }
            }

            if (canMakeZero(nums, allowedOps))
            {
                ans = mid;
                high = mid - 1;
            }
            else
            {
                low = mid + 1;
            }
        }
        return ans;
    }
};

