/*
n = nums.length
m = queries.length
k = max(nums)

states[i] := HashSet of possible states for nums[i]
query := [left, right, val]
for each state in states[left, right]: add (state - val) if >= 0
check whether 0 in each states[i]
=> TC: O(mnk), SC: O(nk)
*/
class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        Set<Integer>[] states = new Set[n];
        for (int i = 0; i < n; i++) {
            states[i] = new HashSet<>();
            states[i].add(nums[i]);
        }
        if (check(states)) {
            return 0;
        }
        for (int i = 0; i < queries.length; i++) {
            int left = queries[i][0];
            int right = queries[i][1];
            int val = queries[i][2];
            for (int j = left; j <= right; j++) {
                Set<Integer> toAdd = new HashSet<>();
                for (int state : states[j]) {
                    int newState = state - val;
                    if (newState >= 0) {
                        toAdd.add(newState);
                    }
                }
                for (int state : toAdd) {
                    states[j].add(state);
                }
            }
            if (check(states)) {
                return i + 1;
            }
        }
        return -1;
    }

    private boolean check(Set<Integer>[] states) {
        for (Set<Integer> set : states) {
            if (!set.contains(0)) {
                return false;
            }
        }
        return true;
    }
}

