class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // Convert to the problem to
        // For each element nums[i], what's the minimum k to make it 0?
        int ans = INT_MIN;
        for (int i = 0; i < nums.size(); ++i) {
            int res;
            if (nums[i] == 0) res = 0;
            else res = knapsack(i, nums[i], queries);
            ans = max(ans, res);
        }
        return ans == INT_MAX ? -1 : ans;
    }

    int knapsack(int i, int val, vector<vector<int>> & queries) {
        vector<bool> dp(val + 1, false);
        // Find the minimum index that we can add all queries to val
        dp[0] = true;
        for (int k = 0; k < queries.size(); ++k) {
            if (i >= queries[k][0] && i <= queries[k][1]) {
                for (int j = val; j >= queries[k][2]; --j)
                    if (dp[j - queries[k][2]]) {
                        // cout << "i = " << i << ", j = " << j << ", k = " << k << ", queries[k][2] = " << queries[k][2] << endl;
                        dp[j] = true;
                    }
            }
            if (dp.back()) {
                // cout << "i = " << i << ", k = " << k << endl;
                return k + 1;
            }
        }
        return INT_MAX;
    }
};

