class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int zero_count=0,n=nums.size();
        vector<unordered_set<int>> all_possible_values(n);
        for(int i=0;i<n;i++){
            all_possible_values[i].insert(nums[i]);
            if(nums[i]==0){
                zero_count++;
            }
        }
        if(zero_count==n){
            return 0;
        }
        for(int k=0;k<queries.size();k++){
            int l=queries[k][0],r=queries[k][1],v=queries[k][2];
            for(int i=l;i<=r;i++){
                unordered_set<int> t;
                for(auto x: all_possible_values[i]){
                    if(x-v>=0){
                        t.insert(x-v);
                    }
                }
                if(t.find(0)!=t.end()&&all_possible_values[i].find(0)==all_possible_values[i].end()){
                    zero_count++;
                }
                for(auto x: t){
                    all_possible_values[i].insert(x);
                }
            }
            if(zero_count==n){
                return k+1;
            }
        }
        return -1;
    }
};

