class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        isZero = set() 
        n = len(nums)
        canDecrease = [set() for _ in range(n)]
        for i in range(n):
            if nums[i] == 0:
                isZero.add(i)
            else:
                canDecrease[i].add(nums[i])
        if len(isZero)  == n: return 0

        for i in range(len(queries)):
            l, r, val = queries[i]
            for j in range(l, r+1):
                if j in isZero: continue
                if val in canDecrease[j]: 
                    isZero.add(j)
                else:
                    tmp = list(canDecrease[j])
                    for v in tmp:
                        if val < v: 
                            canDecrease[j].add(v - val)

            if len(isZero)  == n: return i + 1
        return -1

