class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        dp = [set([0]) for _ in range(n)]

        if all(num == 0 for num in nums):
            return 0

        for k in range(len(queries)):
            l, r, v = queries[k]

            for j in range(l, r + 1):
                new_values = set()
                for val in dp[j]:
                    new_values.add(val + v)
                dp[j].update(new_values)

                dp[j] = {val for val in dp[j] if val <= nums[j]}

            all_achieved = True
            for j in range(n):
                if nums[j] not in dp[j]:
                    all_achieved = False
                    break

            if all_achieved:
                return k + 1

        return -1

