            dp[i][0] = True

        for i in range(1, n + 1):
            for j in range(1, x + 1):
                if arr[i-1] > j:
                    dp[i][j] = dp[i-1][j] 
                else:
                    dp[i][j] = dp[i-1][j] or dp[i-1][j - arr[i-1]] 

        return dp[n][x]


    def check(self,num,d,i):

        arr = []

        for key in d[i].keys():
            if key > num:
                continue

            if key == num:
                return True

            x = d[i][key]

            for _ in range(min(x, num//key + 1)):
                arr.append(key)

        arr.sort()

        return self.isSubsetSum(arr,num)


    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        d = [{} for _ in range(n)]

        currIndex = 0

        q = len(queries)

        for i, num in enumerate(nums):
            if num == 0:
                continue

            while not self.check(num,d,i) and currIndex < q:
                l,r,val = queries[currIndex]

                for j in range(l,r+1):
                    d[j][val] = d[j].get(val,0) + 1

                currIndex += 1

            if currIndex == q and not self.check(num,d,i):
                return -1


        return currIndex

