class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int max_j = -1;

        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                continue;
            }

            vector<pair<int, int>> applicable;
            for (int j = 0; j < queries.size(); ++j) {
                int l = queries[j][0];
                int r = queries[j][1];
                int val = queries[j][2];
                if (i >= l && i <= r) {
                    applicable.emplace_back(j, val);
                }
            }

            unordered_map<int, int> dp;
            dp[0] = -1;

            for (auto& q : applicable) {
                int j_original = q.first;
                int val = q.second;

                unordered_map<int, int> temp;
                for (auto& entry : dp) {
                    int s = entry.first;
                    int new_s = s + val;
                    if (new_s > nums[i]) {
                        continue;
                    }
                    if (temp.find(new_s) == temp.end() || j_original < temp[new_s]) {
                        temp[new_s] = j_original;
                    }
                }

                for (auto& entry : temp) {
                    int s = entry.first;
                    int j = entry.second;
                    if (dp.find(s) == dp.end() || j < dp[s]) {
                        dp[s] = j;
                    }
                }
            }

            if (dp.find(nums[i]) == dp.end()) {
                return -1;
            }
            int earliest_j_i = dp[nums[i]];
            max_j = max(max_j, earliest_j_i);
        }

        return (max_j == -1) ? 0 : (max_j + 1);
    }
};

