    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;
        int lo = 0, hi = queries.size();
        while (lo <= hi){
            int mid = (lo + hi)/2;
            bool ok = true;
            for (int i = 0;i < nums.size();i++){
                bool possible[1001];
                for (int j = 0;j <= 1000;j++){
                    possible[j] = false;
                }
                possible[nums[i]] = true;
                for (int j = 0;j < mid;j++){
                    if (queries[j][0] <= i and queries[j][1] >= i){
                        for (int k = 0;k <= 1000 - queries[j][2];k++){
                            if (possible[k + queries[j][2]]){
                                possible[k] = true;
                            }
                        }
                    }
                }
                if (!possible[0]){
                    ok = false;
                }
            }
            if (ok){
                ans = mid;
                hi = mid - 1;
            }
            else{
                lo = mid + 1;
            }
        }
        return ans;
    }
