class Solution {
public:
    vector<bool> dp;
    bool ispossible(vector<int>& nums, vector<vector<int>>& queries, int k) {
        for (int j = 0; j <nums.size(); j++) {
            dp.assign(1001, false);dp[0]=true;
            for (int i=0;i<k;i++) {
                int l=queries[i][0];
                int r=queries[i][1];
                int val=queries[i][2];
                if (j>=l&&j<=r) {
                    for (int k=1000-val;k>=0;k--) {
                        int newpos=k+val;
                        if (dp[k]){
                            dp[newpos] = true;
                        }
                    }
                }
            }
            if (!dp[nums[j]]) return false;
        }
        return true;
    }
    int search(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size(), ans = -1;
        while(l<=r){
            int mid = l+(r-l)/2;
            if(ispossible(nums, queries, mid)) {
                ans = mid;
                r=mid-1;
            }
            else l=mid+1;
        }
        return ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        return search(nums, queries);
    }
};

