    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        todo = set()
        backet = []
        for i,v in enumerate(nums):
            if v == 0:
                backet.append(set())
            else:
                backet.append({v})
                todo.add(i)

        if not todo:
            return 0

        k = 0
        for L,R,V in queries:
            k += 1
            for idx in todo.copy():
                if idx >= L and idx <= R:
                    if V in backet[idx]:
                        backet[idx] = set()
                        todo.remove(idx)
                    else:
                        for val in backet[idx].copy():
                            if V < val:
                                backet[idx].add(val - V)

            if not todo:
                break

        if todo:
            return -1

        return k

