class Solution {
    public:
        int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

            int n = nums.size();
            int q = queries.size();

            const int THRESHOLD = 1 << 29;
            const int INF = 1 << 30;

            vector<int> ans_list(n, INF);

            for (int i = 0; i < n; i++) {

                int x = nums[i];
                int ans = INF;
                vector<int> dp(x + 2, INF);
                dp[0] = 0;

                for (int j = 0; j < q; j++) {

                    int left = queries[j][0];
                    int right = queries[j][1];
                    int v = queries[j][2];

                    if (i < left || right < i) {
                        continue;
                    }

                    for (int k = x; -1 < k; k--) {

                        if (dp[k] == INF) {
                            continue;
                        }

                        int index = min(x + 1, k + v);
                        dp[index] = min(dp[index], j + 1);
                    }
                }

                ans_list[i] = dp[x];
                // cout << i << " " << nums[i] << " " << dp[x] << endl;
            }

            int ans = *max_element(ans_list.begin(), ans_list.end());
            ans = (ans < THRESHOLD ? ans : -1);

            return ans;
        }
    };

