class Solution {
public:
    int solve(int x, int index, vector<vector<int>> &queries) {
        int q = queries.size();
        vector<vector<bool>> dp(q + 1, vector<bool> (1001, false));
        dp[0][0] = true;

        // dp[q][x] = dp[q - 1][x] or (dp[q - 1][x - queries[q][2]] and (i <= r and i >= l));
        for(int i = 1; i <= q; i++) {
            for(int j = 0; j <= x; j++) {
                dp[i][j] = dp[i - 1][j];
                if(index <= queries[i - 1][1] and index >= queries[i - 1][0] and j >= queries[i - 1][2]) {
                    dp[i][j] = dp[i][j] or dp[i - 1][j - queries[i - 1][2]];
                }
            }
        }

        int l = 0, r = q, ans = -1;
        while(l <= r) {
            int m = l + (r - l) / 2;
            if(dp[m][x]) {
                ans = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }

        return ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = -1;
        for(int i = 0; i < n; i++) {
            int res = solve(nums[i], i, queries);
            if(res == -1) return -1;

            ans = max(ans, res);
        }

        return ans;
    }
};

