class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        int[] originalNums = Arrays.copyOf(nums, n);
        int left = 0, right = m, ans = -1;

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (canMakeZero(nums, queries, mid)) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
            nums = Arrays.copyOf(originalNums, n);
        }
        return ans;
    }

    boolean canMakeZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        List<List<Integer>> appliedDecrements = new ArrayList<>();
        for (int i = 0; i < n; i++) appliedDecrements.add(new ArrayList<>());

        for (int i = 0; i < k; i++) {
            int li = queries[i][0], ri = queries[i][1], vali = queries[i][2];
            for (int j = li; j <= ri; j++) {
                appliedDecrements.get(j).add(vali);
            }
        }
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            List<Integer> values = appliedDecrements.get(i);
            if (!canSelectSubset(values, target)) 
                return false;
        }
        return true;
    }

    boolean canSelectSubset(List<Integer> values, int target) {
        int m = values.size();
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int val : values) {
            for (int sum = target; sum >= val; sum--) {
                dp[sum] |= dp[sum - val];
            }
        }
        return dp[target];
    }
}

