class Solution
{
    public:


//     super interesting constraints
//     n <= 10, bitmask, val <= 10 as well;
//     q.size() <=  1000?
//     can choose non empty subset as well
//     decrementing the number by exactly val
//     

    int const MOD = 1e9 + 7;


    int rec(vector<vector<int>> &queries, vector<vector<int>> &dp, vector<int> &idx, int i, int rem)
    {
        // cout<<"i: "<<i<<" rem: "<<rem<<endl;
        int const n = idx.size();
        if (i >= n) return rem == 0 ? 0 : MOD;
        else if (dp[i][rem] != -1) return dp[i][rem];
        else
        {
            int take = MOD;
            if (rem >= queries[idx[i]][2]) take = max(idx[i], rec(queries, dp, idx, i + 1, rem - queries[idx[i]][2]));

            int nottake = rec(queries, dp, idx, i + 1, rem);

            return dp[i][rem] = min(take, nottake);
        }
    }

    int findix(vector<vector<int>>& queries, vector<int> &idx, int const val)
    { // solving for ix in nums, find earliest index to make it possible;
        int const n = idx.size();
        vector<vector<int>> dp(n + 1, vector<int>(val + 1, -1));        
        int ix = rec(queries, dp, idx, 0, val);

        // cout<<ix<<endl;

        return ix;        
    }


    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries)
    {
        int const n = nums.size(), q = queries.size();
        vector<vector<int>> idx(n);

        for (int qi = 0; qi <= q - 1; qi++)
        {
            for (int i = queries[qi][0]; i <= queries[qi][1]; i++) idx[i].push_back(qi);
        }

        int ans = 0;
        for (int i = 0; i <= n - 1; i++)
        {
            if (!nums[i]) continue; // already zero, ignore

            int ix = findix(queries, idx[i], nums[i]);
            if (ix >= q) return -1; // not possible;

            ans = max(ans, ix + 1);
        }

        return ans;
    }
};

