        idx_vals = defaultdict(list)

        for i in range(len(queries)):
            l, r, val = queries[i]

            for j in range(l, r+1):
                if nums[j] >= val:
                    idx_vals[j].append((val, i))

        # print(idx_vals)

        max_idx = -1
        for i in range(len(nums)):
            if nums[i] == 0:
