            if len(idx_vals[i]) == 0:
                return -1

            min_idx = inf
            tmp_set = set()

            for j in range(len(idx_vals[i])):
                val, qi = idx_vals[i][j]
                # print(j, val, qi)
                if val == nums[i]:
                    min_idx = qi
                    break
                else:
                    new_set = set()
                    flag = False
                    for x in tmp_set:
                        new_val = val+x
                        if new_val == nums[i]:
                            min_idx = qi
                            flag = True
                            break
                        new_set.add(new_val)
                    tmp_set.update(new_set)
                    tmp_set.add(val)
                    if flag:
                        break

            if min_idx == inf:
                return -1
            max_idx = max(max_idx, min_idx)

