class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n = len(nums)

        out = 0
        for i in range(n):
            must = self.gett(queries, i, nums[i])
            # print(f"i{i}, must{must}")
            if must == -1:
                return -1
            out = max(out, must)
        return out

    def gett(self, queries, i, goal_num):

        @cache
        def dp(amount_rem, qpos):

            if amount_rem == 0:
                return qpos
            if qpos == len(queries) or amount_rem < 0:
                return -1


            # try to not choose this num
            hr = dp(amount_rem, qpos+1)

            l, r, val = queries[qpos]
            if l<=i<=r:
                # option to choose this num
                btr = dp(amount_rem-val, qpos+1)
                # choose smaller that is not -1
                if min(hr,btr) != -1:
                    return min(hr,btr)
                return max(hr,btr)
            else:
                return hr





        return dp(goal_num, 0)

