class Solution {
    public boolean isPossible(int[] nums , int K , int[][] queries){
        int n = nums.length;
        int q = queries.length;
        boolean[][] dp = new boolean[n][1200];

        for (int i = 0 ; i < n ;i++)
            dp[i][nums[i]] = true;

        for (int i = 0 ; i <=K ; i++){
            boolean[][] ndp = new boolean[n][1200];
            for (int j= 0 ; j < n ; j++)
                for (int k = 0 ; k < 1200  ; k++)
                    ndp[j][k]  = dp[j][k];

            int val = queries[i][2];
            int l = queries[i][0];
            int r = queries[i][1];

            for (int b = l ; b<=r ; b++){
                for (int v = 0 ; v<1200 ; v++){
                    if (v - val>=0 && dp[b][v])
                        ndp[b][v-val] = true;
                }
            }
            int cnt = 0;
            for (int b = 0 ; b<n ; b++){
                if (ndp[b][0])
                    cnt++;
            }
            for (int j = 0 ; j < n ; j++)
                for (int k = 0 ; k < 1200 ; k++)
                    dp[j][k] = ndp[j][k];
            if (cnt==n)
                return true;
        }
        return false;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int q = queries.length;
        int l = 0 , r = q - 1;
        int ans = q;
        boolean z = true;
        for (int i = 0 ; i < nums.length ; i++){
            if (nums[i]!=0)
                z = false;
        }
        if (z)
            return 0;
        while(l<=r){
            int mid = (l + r)/2;
            if (isPossible(nums , mid , queries)){
                ans = Math.min(ans , mid);
                r = mid - 1;
            }
            else
                l = mid + 1;
        }
        if (ans==q)
            return -1;

        return ans + 1;

    }
}

