        @cache
        def dfs(i, j, s):
            if s == 0: return i
            if i == len(queries): return inf
            l, r, v = queries[i]
            res = dfs(i + 1, j, s)
            if s >= v and l <= j <= r:
                res = min(res, dfs(i + 1, j, s - v))
            return res

        res = 0
        for j in range(len(nums)):
            res = max(res, dfs(0, j, nums[j]))
            dfs.cache_clear()
        return -1 if res == inf else res

