    bool fun(int val, vector<int>& arr) {
    int n = arr.size();
    vector<vector<int>> dp(n + 1, vector<int>(val + 1, 0));
    for (int i = 0; i <= n; i++) dp[i][0] = 1;
    for (int i = n - 1; i >= 0; i--) {
        for (int j = 0; j <= val; j++) {
            bool ans = dp[i + 1][j]; 
            if (j >= arr[i]) ans |= dp[i + 1][j - arr[i]]; 
            dp[i][j] = ans;
        }
    }

    return dp[0][val];
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int l = 1,r=(int)queries.size();
        if(count(nums.begin(),nums.end(),0) == n) return 0;
        int ans = -1;
        auto check=[&](int mid)->bool{
            vector<vector<int>> pos(n+1);
            for(int i=0;i<mid;i++){
                for(int j=queries[i][0];j<=queries[i][1];j++){
                    pos[j].push_back(queries[i][2]);
                }
            }
