            // }
            bool flag = 1;
            // cout<<mid<<endl;
            for(int i=0;i<n;i++){
                flag&=fun(nums[i],pos[i]);
            }
            return flag;
        };
        while(l<=r){
            int mid = l + (r-l)/2;
            if(check(mid)) {ans= mid; r = mid-1;}
            else l = mid+1;
        }
        return ans;
    }
};

