class Solution {
public:

    int dp[1001][1001];
    vector<vector<int>>q;
    int N;
    int solve(int i , int j , int &id) {
        if(i < 0) {
            return 1e9;
        }
        if(i == 0) {
            return j;
        }
        if(j == N) {
            return 1e9;
        }
        if(dp[i][j] != -1) return dp[i][j];

        int ans = 1e9;
        ans = min(ans,solve(i,j+1,id));

        int L = q[j][0];
        int R = q[j][1];
        int val = q[j][2];
        if(L <= id && R >= id) {
            ans = min(ans,solve(i-val,j+1,id));
        }

        return dp[i][j] = ans;

    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& _q) {
        q = _q;
        int n = (int)a.size();
        int ans = 0;
        N = (int)q.size();
        for(int i = 0 ; i < n ; i++) {
            int x = a[i];
            if(x == 0) {
                continue;
            }
            for(int j = 0 ; j <= x ; j++) {
                for(int k = 0 ; k <= N ; k++) {
                    dp[j][k] = -1;
                }
            }

            int can = solve(x,0,i);
            ans = max(ans,can);
        }
        if(ans > N) {
            return -1;
        }
        return ans;
    }
};

