class Solution {
    int[][] cache;

    private int steps(int[][] queries, int i, int idx, int sum) {
        if(sum == 0)  return i;
        else if(i >= queries.length) return Integer.MAX_VALUE;
        else if(cache[i][sum] > 0) return cache[i][sum];

        // System.out.println(i  + " " + sum);

        int l = queries[i][0];
        int r = queries[i][1];
        int val = queries[i][2];

        int min = Integer.MAX_VALUE;
        if(idx >= l && idx <= r && (sum - val) >= 0) {
            min = steps(queries, i + 1, idx, sum - val);
        }
        min = Math.min(min, steps(queries, i + 1, idx, sum));

        cache[i][sum] = min;

        return min;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int max = 0;
        for(int i = 0; i < nums.length; i++) {
            cache = new int[1001][1001];
            int result = steps(queries, 0, i, nums[i]);
            if(result == Integer.MAX_VALUE) return -1;
            max = Math.max(result, max);
        }

        return max;
    }
}

