            for (auto it : queries) {
                int l = it[0], r = it[1], val = it[2];
                if (i >= l && r >= i) {
                    for (int j = v[i] - 1; j >= 0; j--) {
                        if (j + val <= v[i]) {
                            dp[j + val] |= dp[j];
                        }
                    }
                }

                if (dp[v[i]]) {
                    ok = true;
                    break;
                }
                cnt++;
            }

            if (!ok) {
                return -1;
            }

            ans = max(ans, cnt);
        }

        return ans;
    }
};

