class Solution:
    def minZeroArray(self, nums, queries):
        n = len(nums)
        dp_state = [{} for _ in range(n)]
        for i in range(n):
            dp_state[i][0] = True
        if sum(nums) == 0:
            return 0

        for idx, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                new_values = set()
                for key in dp_state[i]:
                    new_values.add(key + v)

                for val in new_values:
                    if val <= nums[i]:
                        dp_state[i][val] = True
            if all(nums[i] in dp_state[i] for i in range(n)):
                return idx + 1  
        return -1

