class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        dp = [[False]*(num+1) for num in nums]
        unfinished = set(range(n))
        for idx,num in enumerate(nums):
            if num==0:
                unfinished.remove(idx)
            dp[idx][0] = True

        if not unfinished:
            return 0

        for k, (L,R,val) in enumerate(queries, 1):
            for idx in range(L,R+1):
                if idx not in unfinished:
                    continue
                num = nums[idx]
                arr = dp[idx]
                lazyLst = []
                for i,b in enumerate(arr):
                    if b:
                        if i+val>=num:
                            if i+val==num:
                                arr[-1] = True
                                unfinished.remove(idx)
                                lazyLst = []
                            break
                        # else:
                        lazyLst.append(i+val)
                for i in lazyLst:
                    arr[i] = True
            if not unfinished:
                return k

        return -1

