class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ans = INT_MAX;
        //each element in nums will have a knapsack to take from
        //for each knapsack, find if its possible to make it zero
        auto possible = [&](int x)->bool{
            //we take the first x queries
            vector<vector<int>> v(n);
            for(int i=0;i<x;i++){
                int left = queries[i][0], right = queries[i][1], val = queries[i][2];
                for(int j=left;j<=right;j++){
                    v[j].push_back(val);
                }
            }
            for(int i=0;i<n;i++){
                vector<bool> dp(nums[i]+1,false);
                dp[0]=true;
                for(auto&it:v[i]){
                    for(int j=nums[i];j>=it;j--){
                        dp[j]=(dp[j]|dp[j-it]);
                    }
                }
                if(!dp[nums[i]]) return false;
            }
            return true;
        };
        int l = 0, r = m;
        while(l<=r){
            int x = (l+r)/2;
            if(possible(x)){
                ans = x;
                r = x - 1;
            }else{
                l = x + 1;
            }
        }
        if(ans==INT_MAX)
            return -1;
        return ans;
    }
};

