from collections import defaultdict
import bisect
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def feasible(k):
            for i in range(n):
                target = nums[i]
                if target == 0:
                    continue

                coins = []
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        coins.append(v)


                dp = 1  
                mask = (1 << (target + 1)) - 1
                for coin in coins:
                    dp |= dp << coin  
                    dp &= mask        
                    if dp & (1 << target):
                        break
                if not (dp & (1 << target)):
                    return False
            return True

        if all(x == 0 for x in nums):
            return 0

        lo, hi = 1, m + 1
        ans = -1
        while lo < hi:
            mid = (lo + hi) // 2
            if feasible(mid):
                ans = mid
                hi = mid
            else:
                lo = mid + 1
        return ans if ans != -1 and ans <= m else -1

