class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>>vec = queries;

        auto solve = [&](int k)->bool{ //lambda define first k queries check karne ke liye
            for(int i=0;i<n;i++){
                int t = nums[i];
                if(t==0){ //agar target 0 hai subtraction not needed
                    continue;
                }
                vector<int>avail;
                for(int j=0;j<k;j++){
                    int l = vec[j][0];
                    int r = vec[j][1];
                    int val = vec[j][2];
                    if(l<=i && i<=r){ // check karna hai idx i range me hai [l,r]
                        avail.push_back(val);
                    }
                }
                vector<bool>dp(t+1,false);
                dp[0] = true;
                for(int idx = 0;idx<avail.size();idx++){
                    int qVal = avail[idx];
                    for(int s = t;s>=qVal;s--){
                        if(dp[s-qVal]){
                            dp[s] = true;
                        }
                    }
                }
                if(!dp[t])
                    return false;
            }
            return true;
        };
        //binary search
        int low = 0,high = m+1;
        while(low<high){
            int mid = low + (high-low)/2;
            if(solve(mid)){
                high= mid;
            }else{
                low = mid+1;
            }
        }
        if(low==m+1)
            return -1;
        return low;
    }
};

