class Solution {
public:

    bool isSubsetSum(vector<int> &arr, int sum){
        int n = arr.size();
        if(!n) return (sum == 0);
        if(!sum) return 1;
        vector<bool> prev(sum + 1, 0), cur(sum + 1, 0);
        prev[0] = cur[0] = 1;
        prev[arr[0]] = 1;

        for(int i = 1; i < n; i++){
            for(int tar = 1; tar <= sum; tar++){

                bool take = 0, not_take = 0;
                if(prev[tar]) not_take = 1;
                if(tar >= arr[i] && prev[tar - arr[i]]) take = 1;

                cur[tar] = (take | not_take);
            }
            prev = cur;
        }
        return prev[sum];
    }
    int minZeroArray(vector<int>& v, vector<vector<int>>& query) {
        int n = v.size(), q = query.size();

        auto check = [&] (int len) -> bool {
            vector<vector<int>> u(n);
            for(int i = 0; i <= len; i ++){
                int l = query[i][0], r = query[i][1], val = query[i][2];
                for(int j = l; j <= r; j ++){
                    u[j].push_back(val);
                }
            }
            for(int i = 0; i < n; i++){
                if(!isSubsetSum(u[i], v[i])) return false;
            }
            return true;
        };
        int c = 0;
        for(int i = 0; i < n; i ++) if(v[i] == 0) c ++;
        if(c == n) return 0;
        int lo = 0, hi = q - 1, m, ans = -1;
        while(lo <= hi){
            m = lo + (hi - lo) / 2;
            if(check(m)) {
                ans = m;
                hi = m - 1;
            }
            else lo = m + 1;
        }
        return (ans == -1 ? ans : ans + 1);
    }
};

