            for(int j=0;j<=10;j++) Arrays.fill(dp[j],-1);
            int ret = f(red[i],11,0,nums[i]);
            if(ret==0) return false;
        }
        return true;
    }
    public int f(int a[], int n, int i,int sum)
    {
        if(sum==0) return 1;
        if(i==n)
        {
            return 0;
        }
        if(dp[i][sum]!=-1) return dp[i][sum];
        int cnt = a[i];
        for(int j=0;j<=cnt;j++)
        {
            if(j*i>sum) break;
            int ret = f(a,n,i+1,sum-j*i);
            if(ret==1) return dp[i][sum] = 1;
        }
        return dp[i][sum] = 0;
    }
}

