class Solution {
    public boolean binary(int[] nums, int k,int[][] queries){
        int n=nums.length;
        for(int i=0;i<n;i++){
            int tar=nums[i];
            boolean[] dp=new boolean[tar+1];
            dp[0]=true;
            for(int j=0;j<k;j++){
                if(queries[j][0]<=i && i<=queries[j][1]){
                    int val=queries[j][2];
                    for(int l=tar;l>=val;l--){
                        if(dp[l-val]) dp[l]=true;
                    }
                }
            }
            if(!dp[tar]) return false;
        }

        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int st=0,end=queries.length;
        int[][] temp=queries;
        int ans=-1;
        while(st<=end){
            int mid=st+(end-st)/2;
            if(binary(nums,mid,temp)){
                ans=mid;
                end=mid-1;
            }
            else{
                st=mid+1;
            }
        }
        return ans;
    }
}

