    int dfs(int x, vector<int>& vals, int index, vector<vector<int>>& q) {
        if (x == 0) return vals[index-1];  //  x  0 
        if (index >= vals.size()) return INT_MAX;  // 

        if (memo.count(x) && memo[x].count(index)) return memo[x][index]; // 

        int res = INT_MAX;
        int minus = q[vals[index]][2];

        // 
        if (x >= minus) {
            res = min(res, dfs(x - minus, vals, index + 1, q));
        }

        // 
        res = min(res, dfs(x, vals, index + 1, q));

        return memo[x][index] = res;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        vector<vector<int>> vals(n, vector<int>());

        for(int i = 0; i < q.size(); ++i) {
            int l = q[i][0], r = q[i][1];
            for(int j = l; j <= r; ++j){
                vals[j].push_back(i);
            }
        }

        int ans = -1;
        for(int i = 0; i < n; ++i) {
            if(nums[i] == 0) {
                ans = max(ans, 0);
                continue;
            }
            memo.clear(); //  `nums[i]`  memo
            int res = dfs(nums[i], vals[i], 0, q);
            if(res == INT_MAX) return -1;
            ans = max(ans, res + 1);
        }

        return ans;
    }
};

