class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def can_form_subset_sum(vals, target):
            if target < 0:
                return False
            reachable = {0}
            for v in vals:
                new_set = set(reachable)  
                for s in reachable:
                    s2 = s + v
                    if s2 == target:
                        return True
                    if s2 < target:
                        new_set.add(s2)
                reachable = new_set
                if target in reachable:
                    return True
            return (target in reachable)
        def feasible(k: int) -> bool:
            coverage_vals = [[] for _ in range(n)]
            for i in range(k):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    coverage_vals[j].append(val)
            for j in range(n):
                if not can_form_subset_sum(coverage_vals[j], nums[j]):
                    return False
            return True
        for k_ in range(m + 1):
            if feasible(k_):
                return k_
        return -1

