class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        dp = {i: set([x]) for i, x in enumerate(nums) if x > 0}
        if not dp:
            return 0

        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r+1):
                if j in dp:
                    tmp = set()
                    for k in dp[j]:
                        if k - val == 0:
                            del dp[j]
                            break
                        if k - val > 0:
                            tmp.add(k-val)
                    if j in dp:
                        dp[j] |= tmp

            if not dp:
                return i + 1
        return -1

