const int inf = 1000;

bool dp[11][inf + 1][inf + 1] = {};

class Solution {
public:

	bool contains(int idx, vector<int>& q) {
		return q[0] <= idx && idx <= q[1];
	}

	int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
		int n = nums.size(), q = queries.size();

		for (int idx = 0; idx < n; idx++) {
			for (int i = 0; i < q; i++) {
				for (int val = 0; val <= inf; val++) {
					if (val == 0) {
						dp[idx][i][val] = 1;
					} else if (contains(idx, queries[i])) {
						dp[idx][i][val] = queries[i][2] == val;
						if (i && !dp[idx][i][val]) {
							dp[idx][i][val] = dp[idx][i - 1][val];
							if (!dp[idx][i][val] && queries[i][2] <= val) {
								dp[idx][i][val] = dp[idx][i - 1][val - queries[i][2]];
							}
						}
					}
					else {
						dp[idx][i][val] = 0;
						if (i) dp[idx][i][val] = dp[idx][i - 1][val];
					}
				}
			}
		}

		if (count(nums.begin(), nums.end(), 0) == n) return 0;
		for (int i = 0; i < q; i++) {
			bool isOkay = 1;
			for (int idx = 0; idx < n; idx++) {
				if (!dp[idx][i][nums[idx]]) {
					isOkay = 0;
					break;
				}
			}
			if (isOkay) return i + 1;
		}
		return -1;
	}
};

