class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        already_zero = all(num == 0 for num in nums)
        if already_zero:
            return 0

        available = [[] for _ in range(n)]
        for j, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                available[i].append((j, val))

        low, high, answer = 1, len(queries), -1
        while low <= high:
            mid = (low + high) // 2
            if self.can_zero(nums, available, mid):
                answer = mid
                high = mid - 1
            else:
                low = mid + 1
        return answer

    def can_zero(self, nums: List[int], available: List[List[tuple[int, int]]], k: int) -> bool:
        for i, target in enumerate(nums):
            dp = [False] * (target + 1)
            dp[0] = True
            for op in available[i]:
                if op[0] < k:
                    val = op[1]
                    for s in range(target, val - 1, -1):
                        if dp[s - val]:
                            dp[s] = True
            if not dp[target]:
                return False
        return True

