class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;
        int loW = 0, higH = q, optimal = -1;

        while (loW <= higH) {
            int mid = loW + (higH - loW) / 2;
            if (canReduceToZero(nums, queries, n, mid)) {
                optimal = mid;
                higH = mid - 1;
            } else {
                loW = mid + 1;
            }
        }
        return optimal;
    }

    private boolean canReduceToZero(int[] nums, int[][] queries, int n, int limit) {
        for (int index = 0; index < n; index++) {
            Set<Integer> possibleSums = new HashSet<>();
            possibleSums.add(0);

            for (int i = 0; i < limit; i++) {
                int start = queries[i][0];
                int end = queries[i][1];
                int decrement = queries[i][2];

                if (index >= start && index <= end) {
                    List<Integer> tempSums = new ArrayList<>(possibleSums);
                    for (int sum : tempSums) {
                        possibleSums.add(sum + decrement);
                    }
                }
            }
            if (!possibleSums.contains(nums[index])) {
                return false;
            }
        }
        return true;
    }
}

