class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        sz = len(nums)
        def can_transform(target):
            axis = [{0} for _ in range(sz + 1)]
            for i in range(target + 1):
                s, e, v = queries[i]
                for i in range(s, e + 1):
                    ls = [v]
                    for num in axis[i]:
                        ls.append(num + v)
                    for num in ls:
                        axis[i].add(num)
            for i in range(sz):
                if nums[i] not in axis[i]:
                    return False
            return True

        s, e = -1, len(queries) - 1
        while s + 1 < e:
            mid = (s + e) // 2
            if can_transform(mid):
                e = mid
            else:
                s = mid
        if can_transform(s):
            return s + 1
        if can_transform(e):
            return e + 1
        return -1

